import React, { useEffect, useState,} from 'react';
import logo from './logo.svg';
import './App.css';
import Konva from 'konva';

function App() {

  const [tail,setTail]=useState([] as (number[]))
  var width= window.innerWidth;
  var height= window.innerHeight;
  var stage=  new Konva.Stage({
    container:'container',
    width:width,
    height:height
  })
  var layer = new Konva.Layer();
  stage.add(layer);


  useEffect(()=>{



    var redLine = new Konva.Line({
      points: tail,
      stroke: 'red',
      strokeWidth: 10,
      lineCap: 'round',
      lineJoin: 'round',
      tension: 0.5,
    });
    layer.add(redLine);
    stage.on('mousedown',()=>{
      var mousePos = stage.getPointerPosition();

      console.log("drag started")


      stage.on('mousemove',()=>{
      var mouseMove = stage.getPointerPosition();
      if(mouseMove != null && mousePos!=null){
        setTail([mousePos.x,mousePos.y,mouseMove.x,mouseMove.y])
        mousePos=mouseMove
      }
      })
      stage.on('mouseup',()=>{


        console.log('drag ended')
        stage.off('mousemove')
        stage.off('mouseup')
      })

    })


  },[tail])




  return (
    <div id="container" className="App">
      Hello
    </div>
  );
}

export default App;
